import Comments from '../Comments/Comments';
import './CommentsList.scss';
import mohanavatar from '../../assets/images/Mohan-muruge.jpg';

const CommentsList = (props) => {
  console.log(props)
  return (
    <>
    <section class="comment__wrap">
      <h3 class="comment__title">
        3 comments
      </h3>
      <article class="comment__container">
        <img src={mohanavatar} class="comment__avatar"/>
        <form class="comment__form">
          <label class="comment__form-label">
            JOIN THE CONVERSATION
          </label>
          <textarea class="comment__form-comment">
            Add a new comment
          </textarea>
          <button class="comment__form-submit">
            comment__form-submit
          </button>
        </form>
      </article>

                          </section>
      {
        props.comments.map(comment => <Comments
            name={comment.name}
            timestamp={comment.timestamp}
            comment={comment.comment}
          />
          )
      }
    </>
  )
}




export default CommentsList;
