import axios from 'axios';
import { Component } from 'react';
import Hero from '../../components/Hero/Hero';
import Main from '../../components/Main/Main';
import CommentsList from '../../components/CommentsList/CommentsList';
// import NextList from '../../components/NextList/NextList';
import './Home.scss';

const API_URL = "https://project-2-api.herokuapp.com" 
const API_KEY_STRING = "?api_key=<78719564-cc2a-4348-85e3-b4d530c557d7>"

// Use get data to populate the state video
// then default fill in selectedVid{}

class Home extends Component {
  state = {
    videos: [],
    selectedVid: {}, 
  };


  // This function is for selecting videos
  getSelectedVid = (vidId) => {
    axios.get(`${API_URL}/video/${vidId}${API_KEY_STRING}`)
    .then((response) => {
      console.log(response.data)
      this.setState({
        videos: response.data
      })
    })
    .catch((error) => console.log(error))
  }

  componentDidMount() {
    console.log("we have mounted")

    axios.get(`${API_URL}/videos${API_KEY_STRING}`)
    .then((response) => {
      console.log(response.data);
      this.setState({
        videos: response.data
      })

    const vidId = this.props.match.params.id || response.data[0].id
      this.getSelectedVid(vidId)
    })
    .catch((error)=> console.log(error) )
  };

  render() {
    return (
      <>
        {/* <Hero
        selectedVid={this.state.selectedVid}
        />
        <section className="main-content">
          <article>
            <Main
              selectedVid={this.state.selectedVid}
            />
            <CommentsList
            // note this one is a bit different because we are accessing a deeper level
              comments={this.state.selectedVid.comments}
            />
          </article>
          <article className="main-content__next-vid-box">
            <NextList
            // we will process this array later with filter
              onVidSelect={this.handleVidSelect}
              nextVids={nextVidFilterArr}
            /> 
          </article>
        </section> */}
      </>
    );
  }
}

export default Home;
