import './Hero.scss';

const posterPath = (imageURL) => `https://i.imgur.com/${imageURL}`

function Hero(props) {
  return (
    <section className="hero">
      <video poster={posterPath(props.image)} alt={`${props.title} Movie Poster`}controls className="hero__video">

        Your browser does not support the video tag.
      </video>
    </section>
  )
}

export default Hero;
