import './Next.scss'


function Next(props) {
  
  const NavigationItem = ({vidName, id, onVidSelect}) => {
    //once the button is clicked we are called the function that was passed through onPlantSelect
    const handleClick = (event) => {
        event.preventDefault()
        //this props has the function handlePlantSelect as a value
        onVidSelect(id)
    }
  }
  return (
    <>

      <article className="next__card" onClick={handleClick}>
        <div className="next__card-img-ctnr">
          <img className="next__card-img" src={props.image} alt={`${props.title} Mini Poster`}></img>
        </div>
        <div className="next__txt_ctnr">
          <div className="next__title">
            {props.title}
          </div>
          <div className="next__channel">
            {props.channel}
          </div>
        </div>
      </article>

  </>
  )
}

export default Next;
