import { Component } from 'react';
import { BrowserRouter, Redirect, Route, Switch } from "react-router-dom";
import Upload from './pages/Upload/Upload';
import Header from './components/Header/Header';
import Home from './pages/Home/Home';
import './App.scss';
import vidDetArr from './data/videodetails.json';
import vidArr from './data/videos.json'

class App extends Component {
  state = {
    selectedVid: vidDetArr[0],
    nextVidDet: vidArr
  }

  handleVidSelect = (id) => {
    this.setState({
      selectedVid: vidDetArr.find((vid) => vid.id === id)
    })
  };

  render () {
    document.title = "Robert So Brainflix";
    // filter out the video selected
    const nextVidFilterArr = vidArr.filter((video)=> video.id !==  this.state.selectedVid.id)
    
    return(
      <div className="App">
        <BrowserRouter>
          <Header />

    {/* // content below is be outside of header */}
            <Switch>
              {/* Default load the default video in the homepage */}
              <Route 
              path="/" 
              exact 
              render={() => {
                return <Home selectedVid={this.state.selectedVid}/>;}}
              />
              {/* load the correct video based on ID */}
              <Route path="/video/:videoID" exact component={Home} />
              {/* Takes you to the upload page */}
              <Route path="/upload" exact component={Upload} />


              
            </Switch>
        </BrowserRouter>
      </div>
  )}
}

export default App;
