import { Component } from 'react';
import Header from './components/Header/Header';
import Hero from './components/Hero/Hero';
import Main from './components/Main/Main';
import CommentsList from './components/CommentsList/CommentsList';
import Next from './components/Next/Next';

import vidDetArr from './data/videodetails.json';
import vidArr from './data/videos.json'





  // document.title = "Robert So Brainflix";

class App extends Component {
  state = {
    selectedVid: vidDetArr[0],
    nextVidDet: vidArr
  }

  render () {
    document.title = "Robert So Brainflix";

    return(
    <>
    <div className="App">
      <Header />
      <Hero
        image={this.state.selectedVid.image}
      />
      <Main
        title={this.state.selectedVid.title}
        channel={this.state.selectedVid.channel}
        timestamp={this.state.selectedVid.timestamp}
        views={this.state.selectedVid.views}
        likes={this.state.selectedVid.likes}
        description={this.state.selectedVid.description}
      />
      <CommentsList comments={this.state.selectedVid.comments} />
      <Next /> 
    </div>
    </>
  )}
}

export default App;



